about_js = function(runBeforeShow) { /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'html_12': 'about_html_12',
        'googlemap_13': 'about_googlemap_13',
        'marker_14': 'about_marker_14'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    if (navigator.userAgent.indexOf("IEMobile") != -1) {
        //Fixing issue https://github.com/jquery/jquery-mobile/issues/5424 on Windows Phone
        $("div[data-role=footer]").css("bottom", "-36px");
    }

    Appery.CurrentScreen = 'about';

    /*
     * Nonvisual components
     */
    var datasources = [];

    /*
     * Events and handlers
     */

    // Before Show
    about_beforeshow = function() {
        Appery.CurrentScreen = "about";
        for (var idx = 0; idx < datasources.length; idx++) {
            datasources[idx].__setupDisplay();
        }
    }

    // On Load
    screen_0A97_onLoad = about_onLoad = function() {
        screen_0A97_elementsExtraJS();

        // TODO fire device events only if necessary (with JS logic)
        about_deviceEvents();
        about_windowEvents();
        screen_0A97_elementsEvents();
    }

    // screen window events
    screen_0A97_windowEvents = about_windowEvents = function() {
        $('#about').bind('pageshow orientationchange', function() {
            adjustContentHeightWithPadding();
        });

    }

    // device events
    about_deviceEvents = function() {

        document.addEventListener("deviceready", function() {

        });
    }

    // screen elements extra js
    screen_0A97_elementsExtraJS = about_elementsExtraJS = function() {
        // screen (about) extra code

        /* googlemap_13 */

        $("[name = 'googlemap_13']").wrap("<div/>");
        $("[name = 'googlemap_13']").parent().css("margin-left", $("[name = 'googlemap_13']").css("margin-left"));
        $("[name = 'googlemap_13']").parent().css("margin-right", $("[name = 'googlemap_13']").css("margin-right"));
        $("[name = 'googlemap_13']").css("margin-left", '0');
        $("[name = 'googlemap_13']").css("margin-right", '0');

        var googlemap_13_options = {
            markerSourceName: "googlemap_13_markers",
            latitude: "47.617924",
            longitude: "-122.20048",
            address: "Bellevue, WA",
            zoom: 10,
            showLocationMarker: false
        }

        Appery.__registerComponent('googlemap_13', new Appery.TiggziMapComponent("googlemap_13", googlemap_13_options));
        $("[name='googlemap_13_markers'] [apperytype='marker']").attr("reRender", "googlemap_13");
        $("[name='googlemap_13']").closest("[data-role='page']").bind({
            pageshow: function() {
                if (Appery('googlemap_13') != undefined) {
                    Appery('googlemap_13').refresh();
                }
            }
        });

    }

    // screen elements handler
    screen_0A97_elementsEvents = about_elementsEvents = function() {

        $("a :input,a a,a fieldset label").live({
            click: function(event) {
                event.stopPropagation();
            }
        });

        $('#about_mobilecontainer [name="html_12"]').die().live({
            click: function() {
                if (!$(this).attr('disabled')) {
                    Appery.navigateTo('startScreen', {
                        reverse: false
                    });

                }
            },
        });
        $('#about_mobilecontainer [name="googlemap_13"]').die().live({
            vclick: function() {
                if (!$(this).attr('disabled')) {
                    window.top.location = 'http://goo.gl/maps/G4zl5';

                }
            },
        });

    }

    $("#about").die("pagebeforeshow").live("pagebeforeshow", function(event, ui) {
        about_beforeshow();
    });

    if (runBeforeShow) {
        about_beforeshow();
    } else {
        about_onLoad();
    }

}

$("#about").die("pageinit").live("pageinit", function(event, ui) {
    Appery.processSelectMenu($(this));
    about_js();
});